import React, { useState } from "react";

function PlantCard({plant, setPlants}) {
  const [inStock, setInStock] = useState(true)
  const [isEdit, setIsEdit] = useState(false)
  const [price, setPrice] = useState({ price: ''})

  function handleStockClick(e){
    setInStock(prevValue => !prevValue)
  }

  function handleEdit(e){
    setIsEdit(prevValue => !prevValue)
  }

  function handleChangeSubmit(e) {
    e.preventDefault()
    setPrice(e.target.price.value)
  }

  function handleDelete(e) {
    fetch(`http://localhost:6001/plants/${plant.id}`, {
      method: 'DELETE'
    })
    .then(resp => resp.json())
    .catch(error => console.log(error))

    setPlants(prevValue => prevValue.filter(item => item !==plant))
  }

  return (
    <li className="card">
      <img src={plant.image} alt={plant.name} />
      <h4>{plant.name}</h4>
      <p>Price: {plant.price}</p>
    {isEdit  ? (
      <form onSubmit={handleChangeSubmit}>
        <input type="number" name="price" placeholder="Price" />
        <button type="submit">Apply Change</button>
      </form>
    ) : (
      null
    )}
      {inStock ? (
        <button onClick={handleStockClick} className="primary">In Stock</button>
      ) : (
        <button onClick={handleStockClick}>Out of Stock</button>
      )}
      <button onClick={handleDelete} style={{marginLeft: '5px'}} className="primary">Delete</button>
      <button onClick={handleEdit} style={{marginLeft: '1px'}} className="primary">{isEdit ? 'Cancel' : 'Edit'}</button>
    </li>
  );
}

export default PlantCard;
