    fetch(`http://localhost:6001/plants/${fish.id}`, {
      method: 'DELETE'
    })
    .then(resp => resp.json())
    .catch(error => console.log(error))

    setFish(prevValue => prevValue.filter(item => item !==fish))
  }

  return (
    <li className="card">
      <img src={fish.image} alt={fish.name} />
      <h4>{fish.name}</h4>
