import React from "react";
import PlantCard from "./PlantCard";

function PlantList({plants, search}) {
  return (
    <ul className="cards">{plants.filter(plant => plant.name.toLowerCase().includes(search.toLowerCase()))
      .map(plant => <PlantCard 
      key={plant.id}
      plant={plant}
      />)}</ul>
  );
}

export default PlantList;
